mapper.update:
update t_staff
district_id=#{district_id}, branch_id=#{branch_id}, post_id=#{post_id}, phone=#{phone}, base_pay=#{base_pay}, zbase_pay=#{zbase_pay}, attendance_bouns=#{attendance_bouns}, meal_bouns=#{meal_bouns}, forck_bouns=#{forck_bouns}, update_time=now(), is_become=#{is_become}, entry_date=#{entry_date}
where id=#{id}

mapper.select:
select id,entry_date
from t_staff 
where job_number=#{name}
